import React, {useState, useEffect} from 'react';
function ViewByQuestion(props){
    const [responses, setResponses] = useState(props.responses);
    const [questions, setQuestions] = useState(props.questions);
    const [textQuestionsIndex, setTextQuestionsIndex] = useState(0);

    useEffect(()=>{
        setQuestions(props.questions);
        setResponses(props.responses);
    },[props.responses, props.questions])

    // console.log(responses)
    // console.log(questions)


    let sortedList;
    //---------------for testing---------------------
    if(responses){
        const sortByDate = (list) => {
            return list.sort((a, b) => new Date(a.date) - new Date(b.date));
        }
        let sortedList = sortByDate(responses);

        console.log(sortedList)
    }



    return (
      <div>
          <h1>View by Question</h1>

          {/*text questions*/}
          <div className="LogSelectionBar" style={{display: "flex", justifyContent: "space-between"}}>
              <button
                  // onClick={prev}
              >
                  <h2>{"<"}</h2>
              </button>
              <h2>
                  {sortedList? sortedList[textQuestionsIndex].date : ""
                  }
              </h2>
              <button
                  // onClick={next}
              >
                  <h2>{">"}</h2>
              </button>
          </div>

      </div>
    );
}
export default ViewByQuestion
