import React, {useState, useEffect} from 'react';
function ViewByQuestion(props){
    const [responses, setResponses] = useState(props.responses);
    const [questions, setQuestions] = useState(props.questions);
    const [textQuestions, setTextQuestions] = useState();
    const [textQuestionsIndex, setTextQuestionsIndex] = useState(0);

    useEffect(()=>{
        setQuestions(props.questions);
        setResponses(props.responses);
        // if(props.responses){
        //     setResponses(sortByDate(props.responses))
        // }
        if(props.responses){
            setTextQuestions(sortByDate(filterOnlyTextQ(props.responses)))
        }
    },[props.responses, props.questions])

    //최신날짜 = 0번째
    const sortByDate = (list) => {
        return list.sort((a, b) => new Date(b.date) - new Date(a.date));
    }
    //text question 만 빼와서 담아줘보리기
    const filterOnlyTextQ = (list) => {
        return list.filter(r=> r.response.text !== "");
    }

    // let sortedList;
    //---------------for testing---------------------
    // if(responses){
    //     let sortedList = sortByDate(responses);
    //     console.log(sortedList)
    //     console.log(sortedList[textQuestionsIndex].date)
    // }

    const dateToString = (date) => {
        return date.getFullYear() + " / " + (date.getMonth() + 1) + " / " + date.getDate();
    }
    const prev = ()=>{
        if(textQuestionsIndex < textQuestions.length-1){
            setTextQuestionsIndex(textQuestionsIndex+1);
        }
    }
    const next = ()=>{
        if(textQuestionsIndex>0){
            setTextQuestionsIndex(textQuestionsIndex-1);
        }
    }

    console.log(responses)
    console.log(textQuestions)






    return (
      <div>
          <h1>View by Question</h1>

          {/*text questions*/}
          <div className="TextQuestionData">
              <div className="LogSelectionBar" style={{display: "flex", justifyContent: "space-between"}}>
                  <button onClick={prev}>
                      <h2>{"<"}</h2>
                  </button>
                  <h2>
                      {textQuestions? textQuestions[textQuestionsIndex].date : ""}
                  </h2>
                  <button onClick={next}>
                      <h2>{">"}</h2>
                  </button>
              </div>
              <div>

              </div>
          </div>

      </div>
    );
}
export default ViewByQuestion
